		var countLabel = new Element('span', {'class': 'abnormality-cases'});
		var countLabelContainer = new Element('span', {'class': 'abnormality-cases-container'}).insert("(").insert(countLabel).insert(")");
		item.insert(" ").insert(countLabelContainer);
