		//console.log("set hpo", hpos)
		for (var i = this.getHPO().length - 1; i >= 0; i--) {
			this.removeHPO(this.getHPO()[i]);
		}
        //console.log("hppp",hpos)

        hpos = this.sortHPOs(hpos);

        for (var i = 0; i < hpos.length; i++) {
			this.addHPO(hpos[i]);
		}

