	sortHPOs: function(hpos){
		var resultHPOs = [];
		hpos.forEach(function(hpo){
            if(hpo.hpoPresent && hpo.hpoPresent == 'Present'){
                resultHPOs.push(hpo);
            }
		})
        hpos.forEach(function(hpo){
            if(!hpo.hpoPresent || hpo.hpoPresent == 'Unknown'){
                resultHPOs.push(hpo);
            }
        })
        hpos.forEach(function(hpo){
            if(hpo.hpoPresent == 'Absent'){
                resultHPOs.push(hpo);
            }
        })
		return resultHPOs;

	},
